import pyAesCryptfrom getpass import getpassbufferSize = 64 * 1024print("Hello. This program can encrypt and decrypt files using pyAesCrypt")encrypt_or_decrypt = str(input("Type E to encrypt or D to decrypt: ").lower())def encryption_fun(pwd_input):	return pyAesCrypt.encryptFile("for_encrypt.txt", "encrypted.txt.aes", pwd_input, bufferSize)def decrypt_fun(pwd_input):	pyAesCrypt.decryptFile("encrypted.txt.aes", "decrypted.txt", pwd_input, bufferSize)if encrypt_or_decrypt == "e":	encryption_fun(pwd_input = getpass(prompt="Input your password: "))	print("Encryption is finished successfully")elif encrypt_or_decrypt == "d":	decrypt_fun(pwd_input = getpass(prompt="Input your password: "))	print("Decryption is finished successfully")